import type { Transition } from '@nuxt/types/app'
import type { NuxtOptionsBuild } from '@nuxt/types/config/build'
import type { NuxtOptionsCli } from '@nuxt/types/config/cli'
import type { NuxtOptionsEnv } from '@nuxt/types/config/env'
import type { NuxtOptionsFeatures } from '@nuxt/types/config/features'
import type { NuxtOptionsFetch } from '@nuxt/types/config/fetch'
import type { NuxtOptionsGenerate } from '@nuxt/types/config/generate'
import type { NuxtOptionsHead } from '@nuxt/types/config/head'
import type { NuxtOptionsHooks } from './hooks'
import type { NuxtOptionsGlobals } from '@nuxt/types/config/globals'
import type { NuxtOptionsLoading, NuxtOptionsLoadingIndicator } from '@nuxt/types/config/loading'
import type { NuxtOptionsModule } from '@nuxt/types/config/module'
import type { NuxtOptionsPlugin } from '@nuxt/types/config/plugin'
import type { NuxtOptionsRender } from '@nuxt/types/config/render'
import type { NuxtOptionsRouter } from '@nuxt/types/config/router'
import type { NuxtOptionsRuntimeConfig } from '@nuxt/types/config/runtime'
import type { NuxtOptionsServer } from '@nuxt/types/config/server'
import type { NuxtOptionsServerMiddleware } from '@nuxt/types/config/server-middleware'
import type { NuxtOptionsVueConfiguration } from '@nuxt/types/config/vue-configuration'
import type { NuxtOptionsWatchers } from '@nuxt/types/config/watchers'

export { Module } from '@nuxt/types/config/module'
export { ServerMiddleware } from '@nuxt/types/config/server-middleware'

/**
 * @deprecated Use NuxtConfig instead
*/
export interface Configuration extends Record<string, any> {}

export interface NuxtOptions extends Configuration {
  alias: Record<string, string>
  build: NuxtOptionsBuild
  buildDir: string
  buildModules: NuxtOptionsModule[]
  cli: NuxtOptionsCli
  css: string[]
  dev: boolean
  dir: { [key in 'app' | 'assets' | 'layouts' | 'middleware' | 'pages' | 'static' | 'store']?: string }
  env: NuxtOptionsEnv
  extensions: string[]
  features: NuxtOptionsFeatures
  fetch: NuxtOptionsFetch
  generate: NuxtOptionsGenerate
  export: NuxtOptionsGenerate
  globalName: string
  globals: NuxtOptionsGlobals
  head: NuxtOptionsHead
  hooks: NuxtOptionsHooks
  ignorePrefix: string
  ignore: string[]
  layoutTransition: Transition
  loading: NuxtOptionsLoading | false | string
  loadingIndicator: NuxtOptionsLoadingIndicator | false | string
  /** @deprecated Use ssr option instead */
  mode: 'spa' | 'universal'
  target: 'server' | 'static'
  modern: 'client' | 'server' | boolean
  modules: NuxtOptionsModule[]
  modulesDir: string[]
  plugins: NuxtOptionsPlugin[]
  privateRuntimeConfig: NuxtOptionsRuntimeConfig
  publicRuntimeConfig: NuxtOptionsRuntimeConfig
  render: NuxtOptionsRender
  rootDir: string
  router: NuxtOptionsRouter
  server: NuxtOptionsServer
  serverMiddleware: NuxtOptionsServerMiddleware[]
  srcDir: string
  ssr: boolean
  transition: Transition
  vue: {
    config?: NuxtOptionsVueConfiguration
  }
  watch: string[]
  watchers: NuxtOptionsWatchers
}

export interface NuxtConfig extends Partial<NuxtOptions> {}
